USE U7G_ACS_BO;
#Update LUKB, SOBA signingCertificate for SubIssuerNetworkCrypto
SET @subIssuerIDSOBA = (SELECT id FROM SubIssuer where code = 83340 AND name = 'Baloise Bank SoBa AG');
SET @subIssuerIDLUKB = (SELECT id FROM SubIssuer where code = 77800 AND name = 'Luzerner KB AG');
UPDATE `SubIssuerNetworkCrypto` SET
                                    `signingCertificate`  = '',
                                    `signingCertificateExpiryDate` = '02/02/2022'
WHERE `fk_id_subIssuer` in (@subIssuerIDSOBA, @subIssuerIDLUKB) AND fk_id_network = (SELECT n.id FROM `Network` n WHERE n.code = 'VISA');



#Update UBS signingCertificate for SubIssuerNetworkCrypto
SET @subIssuerIDUBS = (SELECT id FROM SubIssuer where code = 23000 AND name = 'UBS Switzerland AG');
UPDATE `SubIssuerNetworkCrypto` SET
                                    `signingCertificate`  = '',
                                    `signingCertificateExpiryDate` = '02/02/2022'
WHERE `fk_id_subIssuer` = @subIssuerIDUBS AND fk_id_network = (SELECT n.id FROM `Network` n WHERE n.code = 'VISA');